mapper.insert:
INSERT 
INTO cash_flow( 
	dog_id
	, expense_id
	, cash_flow_type
	, price
	, cancel_flag
	, cash_flow_date
	, close_date
	, create_datetime
	, create_user_id
	, update_datetime
	, update_user_id
) 
VALUES ( 
	#{dogId}
	, #{expenseId}
	, #{cashFlowType}
	, #{price}
	, 0
	, #{cashFlowDate}
	, #{closeDate}
	, CURRENT_TIMESTAMP
	, #{createUserId}
	, CURRENT_TIMESTAMP
	, #{updateUserId}
)

mapper.update:
UPDATE cash_flow 
SET
	dog_id = #{dogId}
	, expense_id = #{expenseId}
	, cash_flow_type = #{cashFlowType}
	, price = #{price}
	, cash_flow_date = #{cashFlowDate}
	, close_date = #{closeDate}
	, update_datetime = CURRENT_TIMESTAMP
	, update_user_id = #{updateUserId} 
WHERE
	expense_id = #{expenseId}
	AND fix_flag = 0
